import { View, Text, Button, StyleSheet, Image, Linking, Dimensions } from 'react-native'
import React, { useEffect } from 'react'
import { ScrollView, TouchableOpacity } from 'react-native-gesture-handler'
import AsyncStorage from '@react-native-async-storage/async-storage'
import Icon from 'react-native-vector-icons/FontAwesome';
import {
  heightPercentageToDP as hp,
  widthPercentageToDP as wp,
} from '../../responsiveLayout/ResponsiveLayout'
import moment from 'moment';
import { Grid, Row, Col } from 'react-native-easy-grid'
import { useState } from 'react';
function Landing(props) {

  const [admin, setAdmin] = useState()
  const [sales, setSales] = useState()

  const attendanceHandler = () => {

    props.navigation.navigate('Attendance')
  }

  const attendanceListHandler = () => {

    props.navigation.navigate('Attendance List')
  }


  const callVisitEntryHandler = () => {

    props.navigation.navigate('Call Visit Entry')
  }


  const callVisitEntryListHandler = () => {

    props.navigation.navigate('Call Visit Entry List')
  }



  const callSummaryHandler = () => {

    props.navigation.navigate('Call Summary')
  }


  const claimStatusHandler = () => {

    props.navigation.navigate('Claim Status')
  }

  const comingSoonHandler = () => {

    props.navigation.navigate('Claim Status')
  }



  useEffect(() => {
    const use = async () => {
      console.log("setting")
      var startDate = moment(await AsyncStorage.getItem("startingDate")).format("DD/MM/YYYY")
      var endDate = moment(await AsyncStorage.getItem("endDate")).format("DD/MM/YYYY")
      var divisionCode = await AsyncStorage.getItem("divisionName")
      setAdmin(await AsyncStorage.getItem("administrator"))
      setSales(await AsyncStorage.getItem("salesTeam"))
      props.navigation.setOptions({
        headerTitle: () => {
          return (
            <View style={{ diplay: "flex", flexDirection: "row" }}>
              <Text style={{ textAlign: "center", fontWeight: "bold", fontSize: wp("4.5%"), marginTop: 5 }}>Home
              </Text>
              <View style={{ diplay: "flex", flexDirection: "column", alignItems: "center", marginLeft: 30 }}>
                <Text style={{ fontWeight: "bold", color: "blue" }} >{divisionCode}</Text>
                <Text style={{ fontWeight: "bold", color: "blue" }} >{startDate.substring(3, 10)}-{endDate.substring(3, 10)}</Text>
              </View>
            </View>
          )
        }

      })

    }
    use()
  }, [])


  return (
    <ScrollView style={styles.container}>

      {admin ?
        <Grid>
          <Row style={[styles.container1, { borderBottomColor: "black", borderBottomWidth: 0.5 }]}>
            <Col
              style={[
                styles.container1, { borderRigthColor: "black", borderRightWidth: 0.5 }]}
            >
              <TouchableOpacity

                style={styles.icon}
                activeOpacity={0.8}
                onPress={() => attendanceHandler()}
              >
                <Image source={require("../../../assets/Deal.png")} style={styles.image} resizeMode="contain" />
                <Text style={styles.text}
                >Attendance</Text>

              </TouchableOpacity>


            </Col>
            <Col
              style={[
                styles.container1, { borderRigthColor: "black", borderRightWidth: 0.5 }]}  >

              <TouchableOpacity

                style={styles.icon}
                activeOpacity={0.8}
                onPress={() => callVisitEntryHandler()}
              >
                <Image source={require("../../../assets/contractentry.png")} style={styles.image} resizeMode="contain" />
                <Text style={styles.text}
                >Call Visit Entry</Text>

              </TouchableOpacity>

            </Col>


          </Row>




          <Row style={[styles.container1, { borderBottomColor: "black", borderBottomWidth: 0.5 }]}>
            <Col
              style={[
                styles.container1, { borderRigthColor: "black", borderRightWidth: 0.5 }]}
            >
              <TouchableOpacity

                style={styles.icon}
                activeOpacity={0.8}
                onPress={() => attendanceListHandler()}
              >
                <Image source={require("../../../assets/register.png")} style={styles.image} resizeMode="contain" />
                <Text style={styles.text}
                >Attendance List</Text>

              </TouchableOpacity>


            </Col>
            <Col
              style={[
                styles.container1, { borderRigthColor: "black", borderRightWidth: 0.5 }]}  >

              <TouchableOpacity

                style={styles.icon}
                activeOpacity={0.8}
                onPress={() => callVisitEntryListHandler()}
              >
                <Image source={require("../../../assets/billing.png")} style={styles.image} resizeMode="contain" />
                <Text style={styles.text}
                >Visit List</Text>
              </TouchableOpacity>

            </Col>


          </Row>



          <Row style={[styles.container1, { borderBottomColor: "black", borderBottomWidth: 0.5 }]}>
            <Col
              style={[
                styles.container1, { borderRigthColor: "black", borderRightWidth: 0.5 }]}
            >
              <TouchableOpacity

                style={styles.icon}
                activeOpacity={0.8}
                onPress={() => callSummaryHandler()}
              >
                <Image source={require("../../../assets/register.png")} style={styles.image} resizeMode="contain" />
                <Text style={styles.text}
                >Call Summary</Text>

              </TouchableOpacity>


            </Col>
            <Col
              style={[
                styles.container1, { borderRigthColor: "black", borderRightWidth: 0.5 }]}  >

              <TouchableOpacity

                style={styles.icon}
                activeOpacity={0.8}
                onPress={() => comingSoonHandler()}
              >
                <Image source={require("../../../assets/billing.png")} style={styles.image} resizeMode="contain" />
                <Text style={styles.text}
                >Claim Status</Text>
              </TouchableOpacity>

            </Col>


          </Row>
        </Grid>
        :
        <>
          {!sales ?
            <Grid>
              <Row style={[styles.container1, { borderBottomColor: "black", borderBottomWidth: 0.5 }]}>
                <Col
                  style={[
                    styles.container1, { borderRigthColor: "black", borderRightWidth: 0.5 }]}
                >
                  <TouchableOpacity

                    style={styles.icon}
                    activeOpacity={0.8}
                    onPress={() => attendanceHandler()}
                  >
                    <Image source={require("../../../assets/Deal.png")} style={styles.image} resizeMode="contain" />
                    <Text style={styles.text}
                    >Attendance</Text>

                  </TouchableOpacity>


                </Col>
                <Col
                  style={[
                    styles.container1, { borderRigthColor: "black", borderRightWidth: 0.5 }]}  >

                  <TouchableOpacity

                    style={styles.icon}
                    activeOpacity={0.8}
                    onPress={() => callVisitEntryHandler()}
                  >
                    <Image source={require("../../../assets/contractentry.png")} style={styles.image} resizeMode="contain" />
                    <Text style={styles.text}
                    >Call Visit Entry</Text>

                  </TouchableOpacity>

                </Col>


              </Row>




              <Row style={[styles.container1, { borderBottomColor: "black", borderBottomWidth: 0.5 }]}>
                <Col
                  style={[
                    styles.container1, { borderRigthColor: "black", borderRightWidth: 0.5 }]}
                >
                  <TouchableOpacity

                    style={styles.icon}
                    activeOpacity={0.8}
                    onPress={() => attendanceListHandler()}
                  >
                    <Image source={require("../../../assets/register.png")} style={styles.image} resizeMode="contain" />
                    <Text style={styles.text}
                    >Attendance List</Text>

                  </TouchableOpacity>


                </Col>
                <Col
                  style={[
                    styles.container1, { borderRigthColor: "black", borderRightWidth: 0.5 }]}  >

                  <TouchableOpacity

                    style={styles.icon}
                    activeOpacity={0.8}
                    onPress={() => callVisitEntryListHandler()}
                  >
                    <Image source={require("../../../assets/billing.png")} style={styles.image} resizeMode="contain" />
                    <Text style={styles.text}
                    >Visit List</Text>
                  </TouchableOpacity>

                </Col>


              </Row>
            </Grid>
            :

            <Grid>

              <Row style={[styles.container1, { borderBottomColor: "black", borderBottomWidth: 0.5 }]}>
                <Col
                  style={[
                    styles.container1, { borderRigthColor: "black", borderRightWidth: 0.5 }]}
                >
                  <TouchableOpacity

                    style={styles.icon}
                    activeOpacity={0.8}
                    onPress={() => callSummaryHandler()}
                  >
