                                            defaultValue={endDayMillometerReading}
                                            onChangeText={text => setEndDayMillometerReading(text)}
                                        />

                                        <TextInput
                                            style={[styles.input, { backgroundColor: "#D3FD7A", width: wp('82%'), flex: 0.97 }]}
                                            placeholder="Remarks"
                                            editable={!startDay}
