// import { StatusBar } from 'expo-status-bar';
import React, { useEffect, useState } from 'react';
import { StyleSheet, Text, View, Button, LogBox, NativeModules, Alert, Image, Dimensions } from 'react-native';
import Login from './screens/screen/Login';
import * as Updates from 'expo-updates'
import Company from './screens/screen/Company';
import { createStackNavigator } from '@react-navigation/stack';
import { NavigationContainer } from '@react-navigation/native';
import Icon from 'react-native-vector-icons/FontAwesome';
import Toast from 'react-native-toast-message';
import { ScrollView, TouchableOpacity } from 'react-native-gesture-handler';
import theme1 from './screens/components/styles/DarkTheme.js'
import ComingSoon from './screens/screen/ComingSoon'
import { Avatar } from 'react-native-paper';

import {
  createDrawerNavigator,
  DrawerContentScrollView,
  DrawerItemList,
  DrawerItem,
} from '@react-navigation/drawer';
import {
  heightPercentageToDP as hp,
  widthPercentageToDP as wp,
} from './screens/responsiveLayout/ResponsiveLayout'

import Landing from './screens/Home/Landing/Landing';
import AsyncStorage from '@react-native-async-storage/async-storage';
import Attendance from './screens/Home/Attendance/Attendance';
import CallEntry from './screens/Home/CallEntry/CallEntry';
import AttendanceList from './screens/Home/Attendance/AttendanceList';
import CallEntryList from './screens/Home/CallEntry/CallEntryList';
import CallSummary from './screens/Home/CallSummary/CallSummary';
import ClaimStatus from './screens/Home/ClaimStatus/ClaimStatus';
import AddParty from './screens/Home/AddParty/AddParty';
var u
var administrator
var salesTeam
const width = Dimensions.get('window').width
const height = Dimensions.get('window').height

const Stack = createStackNavigator();
export default function App(props) {


  const [user, setUser] = useState()
  const [loading, setLoading] = useState(false)

  useEffect(() => {
    var isCancelled = false;
    const unsubscribe = async () => {
      u = await AsyncStorage.getItem("user")
      administrator = await AsyncStorage.getItem("administrator")
      salesTeam = await AsyncStorage.getItem('salesTeam')
      setUser(await AsyncStorage.getItem("user"))
      setLoading(true)
    }
    unsubscribe()
    return () => {
      isCancelled = true
    };
  }, [])


  LogBox.ignoreAllLogs(true)
  return (
    <>
      {
        loading &&
        <View style={styles.container}>
          <Toast ref={(ref) => Toast.setRef(ref)} />
          <MyDrawer usera={u} admin={administrator} sales={salesTeam} />
        </View>
      }
    </>
  );
}


export function LoginStack(props) {
  const [user, setUser] = useState()
  return (

    <Stack.Navigator >
      <Stack.Screen name="Login" component={Login} options={({ navigation }) => ({
        headerTitleAlign: "center",
        headerStyle: {
          borderBottomEndRadius: 20, borderBottomStartRadius: 20,
        }, title: !AsyncStorage.getItem("user") ? "Shift Company/Division" : "Login",



      })} />
      <Stack.Screen name="Home" component={Landing} options={({ navigation }) => ({
        headerStyle: {
          backgroundColor: "#D9EDF7"
        }, headerLeft: (props) => {
          return <Icon reverse name="bars" size={wp("7%")} color={theme1.HEADER_TEXT_COLOR} onPress={() => navigation.toggleDrawer()} style={{ marginLeft: 30 }} />
        },

        headerRight: (props) => {
          return (

            <TouchableOpacity
              style={{ marginRight: 30 }}
              onPress={() =>
                Alert.alert("Logout alert", "Do you really want to Logout...", [
                  {
                    text: "NO",
                    onPress: () => console.warn("NO Pressed"),
                    style: "cancel"
                  },
                  {
                    text: "YES",
                    onPress: () => {
                      AsyncStorage.removeItem("user")
                      navigation.navigate("Login");
                      console.log("restarting")
                      Updates.reloadAsync()
                    }

                  }
                ])
              }
            >
              <Icon name="sign-out" size={wp("10%")} style={{ marginLeft: 12 }} />
            </TouchableOpacity>
          )
        },
      })} />

    </Stack.Navigator>

  );
}

export function HomeStack() {

  return (

    <Stack.Navigator>
      <Stack.Screen name="Home" component={Landing} options={({ navigation }) => ({
        headerStyle: {
          backgroundColor: theme1.LIGHT_BLUE_COLOR
        }, headerTitleStyle: {
          color: 'black'
        }, headerLeft: (props) => {
          return <Icon reverse name="bars" size={wp("7%")} color={theme1.HEADER_TEXT_COLOR} onPress={() => navigation.toggleDrawer()} style={{ marginLeft: 30 }} />
        },

        headerRight: (props) => {
          return (

            <TouchableOpacity
              style={{ marginRight: 30 }}
              onPress={() =>
                Alert.alert("Logout alert", "Do you really want to Logout...", [
                  {
                    text: "NO",
                    onPress: () => console.warn("NO Pressed"),
                    style: "cancel"
                  },
                  {
                    text: "YES",
                    onPress: () => {

                      AsyncStorage.removeItem("user")
                      navigation.navigate("Login");
                      Updates.reloadAsync()
                    }

                  }
                ])
              }
            >
              <Icon name="sign-out" size={wp("7%")} />
            </TouchableOpacity>
          )
        },
      })} />
    </Stack.Navigator>

  );
}


export function AttendanceStack() {
  return (

    <Stack.Navigator>
      <Stack.Screen name="Attendance" component={Attendance} options={({ navigation }) => ({
        headerStyle: {
          borderBottomEndRadius: 20, borderBottomStartRadius: 20, backgroundColor: theme1.LIGHT_BLUE_COLOR
        }, headerTitleStyle: {
          color: theme1.HEADER_TEXT_COLOR
        }, headerLeft: (props) => {
          return <Icon reverse name="bars" size={wp("7%")} color={theme1.HEADER_TEXT_COLOR} onPress={() => navigation.toggleDrawer()} style={{ marginLeft: 30 }} />
        }
      })} />
    </Stack.Navigator>

  );
}


