  return (

    <Stack.Navigator>
      <Stack.Screen name="Call Visit Entry List" component={CallEntryList} options={({ navigation }) => ({
        headerStyle: {
          borderBottomEndRadius: 20, borderBottomStartRadius: 20, backgroundColor: theme1.LIGHT_BLUE_COLOR
        }, headerTitleStyle: {
          color: theme1.HEADER_TEXT_COLOR
        }, headerLeft: (props) => {
