        }
      })} />
    </Stack.Navigator>

  );
}



export function ClaimStatusStack() {
  return (

    <Stack.Navigator>
      <Stack.Screen name="Claim Status" component={ClaimStatus} options={({ navigation }) => ({
        headerStyle: {
          borderBottomEndRadius: 20, borderBottomStartRadius: 20, backgroundColor: theme1.LIGHT_BLUE_COLOR, marginTop: 100
        }, headerTitleStyle: {
          color: theme1.HEADER_TEXT_COLOR
        }, headerLeft: (props) => {
          return <Icon reverse name="bars" size={wp("7%")} color={theme1.HEADER_TEXT_COLOR} onPress={() => navigation.toggleDrawer()} style={{ marginLeft: 30 }} />
        },
        headerShown: true
      })} />
    </Stack.Navigator>

  );
}


export function AddPartyStack() {
  return (

    <Stack.Navigator>
      <Stack.Screen name="Add Party" component={AddParty} options={({ navigation }) => ({
        headerStyle: {
          borderBottomEndRadius: 20, borderBottomStartRadius: 20, backgroundColor: theme1.LIGHT_BLUE_COLOR, marginTop: 100
        }, headerTitleStyle: {
          color: theme1.HEADER_TEXT_COLOR
        }, headerLeft: (props) => {
          return <Icon reverse name="bars" size={wp("7%")} color={theme1.HEADER_TEXT_COLOR} onPress={() => navigation.toggleDrawer()} style={{ marginLeft: 30 }} />
