export function Drawerbutton({ navigation }) {
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Button title="Toggle drawer" onPress={() => navigation.toggleDrawer()} />
    </View>
  );
}



export function CustomDrawerContent(props) {
  const [user, setUser] = useState()

  useEffect(() => {
    const getUser = async () => {
      setUser(await AsyncStorage.getItem("user"))
    }
    getUser()
  }, [props.navigation])




  return (
    <>

      <DrawerContentScrollView {...props} style={{ backgroundColor: theme1.DARK_BLUE_COLOR }} >
        <View style={{ marginLeft: wp("12%"), width: wp("100%"), textAlign: "center" }}>
          <Image source={{ uri: `http://103.231.46.238:5000/public/img/logo.png ` }} style={{ height: hp("10%"), width: wp("45%") }} resizeMode="contain" />
        </View>
        <View style={{ display: "flex", flexDirection: "column", width: wp("50%"), top: -20 }}>

          <Text
            style={{
              marginLeft: wp("27%"),
              marginTop: 10, fontWeight: "bold", fontSize: wp("4%")
              , marginRight: 25, color: "yellow", textAlign: "center", marginBottom: 0
            }}>{user}  </Text>
        </View>
        <View style={{ borderBottomColor: 'grey', borderBottomWidth: 1, marginBottom: 0, padding: 0, marginBottom: 10 }} />

        <DrawerItemList {...props} />
        {
          user &&
          <TouchableOpacity
            style={{ marginRight: 30 }}
            onPress={() =>
              Alert.alert("Logout alert", "Do you really want to Logout...", [
                {
                  text: "NO",
                  onPress: () => console.warn("NO Pressed"),
                  style: "cancel"
                },
                {
                  text: "YES",
                  onPress: () => {
                    AsyncStorage.removeItem("user")
                    props.navigation.navigate("Posts");
                    console.log("restarting")
                    Updates.reloadAsync()
                  }

                }
              ])
            }
          >
            <View style={{ display: "flex", flexDirection: "row", paddingLeft: 20, paddingTop: 20 }}>
              <Icon name="sign-out" size={wp("8%")} color="white" style={{ marginLeft: 12 }} />
              <Text style={{ fontSize: wp("4%"), paddingLeft: 18, paddingTop: 3, marginRight: 15, color: "white" }}>Logout</Text>

            </View>
          </TouchableOpacity>
        }

      </DrawerContentScrollView>
    </>
  );
}

const Drawer = createDrawerNavigator();

export function MyDrawer({ usera, admin, sales }) {
  console.log("admin", admin, sales)
  return (
    <NavigationContainer>
      <Drawer.Navigator initialRouteName="Home"
        drawerContent={props => <CustomDrawerContent {...props} />}


        drawerContentOptions={{
          activeTintColor: 'white',
          activeBackgroundColor: '#1B1B1B',
          inactiveTintColor: 'white',

          labelStyle: {
            marginLeft: -10,
            fontSize: wp("4%"),
            width: wp("50%")

          }
        }}>

        {
          !usera &&
          <Drawer.Screen name="Login" component={LoginStack} options={{

            drawerIcon: ({ focused, size }) =>
              <Icon name="copy" reverse size={wp("8%")} color="white" style={{ marginLeft: 8 }} />
          }} />
        }
        {
          usera &&
          <>

            {admin ?
              <>
                <Drawer.Screen name="Home" component={HomeStack}
                  options={{
                    drawerIcon: ({ focused, size }) =>
                      <Icon name="home" reverse size={wp("8%")} color="white" style={{ marginLeft: 8 }} />
                  }} />


                <Drawer.Screen name="Attendance" component={AttendanceStack} options={{

                  drawerIcon: ({ focused, size }) =>
                    <Icon name="copy" reverse size={wp("8%")} color="white" style={{ marginLeft: 8 }} />
                }} />

                <Drawer.Screen name="Call Visit Entry" component={CallVisitEntryStack} options={{

                  drawerIcon: ({ focused, size }) =>
                    <Icon name="copy" reverse size={wp("8%")} color="white" style={{ marginLeft: 8 }} />
                }} />


                <Drawer.Screen name="Attendance List" component={AttendanceListStack} options={{

                  drawerIcon: ({ focused, size }) =>
                    <Icon name="copy" reverse size={wp("8%")} color="white" style={{ marginLeft: 8 }} />
                }} />


                <Drawer.Screen name="Call Visit Entry List" component={CallVisitEntryListStack} options={{

                  drawerIcon: ({ focused, size }) =>
                    <Icon name="copy" reverse size={wp("8%")} color="white" style={{ marginLeft: 8 }} />
                }} />

                <Drawer.Screen name="Call Summary" component={CallSummaryStack} options={{

                  drawerIcon: ({ focused, size }) =>
                    <Icon name="copy" reverse size={wp("8%")} color="white" style={{ marginLeft: 8 }} />
                }} />

                <Drawer.Screen name="Add Party" component={AddPartyStack} options={{

                  drawerIcon: ({ focused, size }) =>
                    <Icon name="copy" reverse size={wp("8%")} color="white" style={{ marginLeft: 8 }} />
                }} />

                <Drawer.Screen name="Claim Status" component={ComingStack}
                  options={{
                    drawerIcon: ({ focused, size }) =>
                      <Icon name="home" reverse size={wp("8%")} color="white" style={{ marginLeft: 8 }} />
                  }} />


              </> :
              <>
                {!sales ?
                  <>
                    <Drawer.Screen name="Home" component={HomeStack}
                      options={{
                        drawerIcon: ({ focused, size }) =>
                          <Icon name="home" reverse size={wp("8%")} color="white" style={{ marginLeft: 8 }} />
                      }} />
                    <Drawer.Screen name="Coming Soon" component={ComingStack}
                      options={{
                        drawerIcon: ({ focused, size }) =>
                          <Icon name="home" reverse size={wp("8%")} color="white" style={{ marginLeft: 8 }} />
                      }} />

                    <Drawer.Screen name="Attendance" component={AttendanceStack} options={{

                      drawerIcon: ({ focused, size }) =>
                        <Icon name="copy" reverse size={wp("8%")} color="white" style={{ marginLeft: 8 }} />
                    }} />

                    <Drawer.Screen name="Call Visit Entry" component={CallVisitEntryStack} options={{

                      drawerIcon: ({ focused, size }) =>
                        <Icon name="copy" reverse size={wp("8%")} color="white" style={{ marginLeft: 8 }} />
                    }} />


                    <Drawer.Screen name="Attendance List" component={AttendanceListStack} options={{

                      drawerIcon: ({ focused, size }) =>
                        <Icon name="copy" reverse size={wp("8%")} color="white" style={{ marginLeft: 8 }} />
                    }} />


                    <Drawer.Screen name="Call Visit Entry List" component={CallVisitEntryListStack} options={{

                      drawerIcon: ({ focused, size }) =>
                        <Icon name="copy" reverse size={wp("8%")} color="white" style={{ marginLeft: 8 }} />
                    }} />
                    <Drawer.Screen name="Add Party" component={AddPartyStack} options={{

                      drawerIcon: ({ focused, size }) =>
                        <Icon name="copy" reverse size={wp("8%")} color="white" style={{ marginLeft: 8 }} />
                    }} />
                  </>
                  :
                  <>
                    <Drawer.Screen name="Home" component={HomeStack}
                      options={{
                        drawerIcon: ({ focused, size }) =>
                          <Icon name="home" reverse size={wp("8%")} color="white" style={{ marginLeft: 8 }} />
                      }} />

                    <Drawer.Screen name="Call Summary" component={CallSummaryStack} options={{

                      drawerIcon: ({ focused, size }) =>
